import React, {Component} from 'react';
import { Router } from "@reach/router";

import Questions from "./Questions";
import Question from "./Question";

class App extends Component {

    constructor(props) {
        super(props);

        // This is my state data initialized
        this.state = {
            questions: []
        }
    }
    componentDidMount() {
        this.getQuestions().then(() => console.log("questions have been gotten"));
    }

    async getQuestions() {
        let url = 'http://localhost:8080/api/questions';
        let result = await fetch(url); //get the data
        let json = await result.json();
        return this.setState({
            answers: json
        })
    }

    getQuestion(id){
        //find question by id
        return this.state.answers.find(a => a._id === id)
    }

    render() {
        return (
            <React.Fragment>
                <h1>QA Website</h1>
                <Router>

                    <Questions path="/" questions={this.state.questions}
                       /* askQuestion={(text) => this.askQuestion(text)}*/>
                    </Questions>
                    <Question path="/question/:id"
                              getQuestion={id => this.getQuestion(id)}></Question>
                </Router>
            </React.Fragment>
        )
    }
}

export default App;
